pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract PolygonToken is ERC20 {
    address public owner;
    uint256 public totalBurnedToken;
    uint256 public totalMintedToken;

    constructor() ERC20("Launch Token", "LT") {
        owner = msg.sender;
        _mint(msg.sender, 1000000 * (10**uint256(decimals())));
    }

    function mint(address to, uint256 amount) public {
        // require(msg.sender == owner, "Caller is not a minter");
        _mint(to, amount);
        totalMintedToken = totalMintedToken + amount;
    }

    function burn(address from, uint256 amount) public {
        // require(msg.sender == owner, "Caller is not a burner");
        _burn(from, amount);
        totalBurnedToken = totalBurnedToken + amount;
    }
}
