pragma solidity ^0.8.0;

import "./PolygonToken.sol";

contract StakingPoll {
    string public name;
    address public owner;
    PolygonToken public pToken;

    address[] public stakers;
    mapping(address => uint256) public stakingBalance;
    mapping(address => bool) public hasStaked;
    mapping(address => bool) public isStaking;

    uint256 public totalStakingBalance;
    uint256 public totalBurnedToken;
    uint256 public totalMintedToken;

    // This unstake is variables used for minting the unstaked tokens.
    mapping(address => uint256) public unStakedUserDetails;
    address[] public unStakers;
    mapping(address => bool) public hasUnstaked;

    // This used for burning the staked tokens.
    uint256 public stakingBalToBeBurned;

    constructor(PolygonToken _pToken, string memory _name) {
        pToken = _pToken;
        name = _name;
        owner = msg.sender;
    }

    function stakeTokens(uint256 _amount) public {
        // Require amount greater than 0
        require(_amount > 0, "amount cannot be 0");

        // Trasnfer Mock Dai tokens to this contract for staking
        pToken.transferFrom(msg.sender, address(this), _amount);

        // Update staking balance
        stakingBalance[msg.sender] = stakingBalance[msg.sender] + _amount;

        // Update staking balance in temp varialble.
        stakingBalToBeBurned = stakingBalToBeBurned + _amount;

        // Add user to stakers array *only* if they haven't staked already
        if (!hasStaked[msg.sender]) {
            stakers.push(msg.sender);
        }

        // Update staking status
        isStaking[msg.sender] = true;
        hasStaked[msg.sender] = true;
    }

    // Unstaking Tokens (Withdraw) -- Just updating the pool.
    function unstakeTokens(uint256 _amount) public {
        // check the amount is greater than 0
        require(_amount > 0, "staking balance cannot be 0");

        // Fetch staking balance
        uint256 balance = stakingBalance[msg.sender];

        // Require amount greater than 0
        require(balance > 0, "staking balance cannot be 0");

        // Check given amount is available in the balace.
        require(
            _amount <= balance,
            "Your staking balace is lesser than the given amount"
        );

        uint256 remaingBalance = balance - _amount;

        // add unstaked user in the unStakedUserDetails mapping.
        unStakedUserDetails[msg.sender] =
            unStakedUserDetails[msg.sender] +
            _amount;

        // add user to unstakers array *only* if they haven't unstake already.
        if (!hasUnstaked[msg.sender]) {
            unStakers.push(msg.sender);
        }

        hasUnstaked[msg.sender] = true;

        // Transfer Mock Dai tokens to this contract for staking
        // pToken.transfer(msg.sender, _amount);

        // mint token to the user who unstake the token.
        // pToken.mint(unstakeUserAddress, _amount);

        // Reset staking balance
        stakingBalance[msg.sender] = remaingBalance;

        // Update staking status
        if (remaingBalance == 0) {
            isStaking[msg.sender] = false;
        }
    }

    function totalStakeBalance() public returns (uint256 bal) {
        uint256 balance;
        for (uint256 i = 0; i < stakers.length; i++) {
            address recipient = stakers[i];
            balance = stakingBalance[recipient] + balance;
        }
        totalStakingBalance = balance;
        return balance;
    }

    // burn all the token in the staking poll
    function burnStakedTokens(address stakingPollContractAddress) public {
        // Only owner can call this function
        require(msg.sender == owner, "caller must be the owner");

        // Require amount greater than 0
        require(stakingBalToBeBurned > 0, "burn amount cannot be 0");

        // call burn function.
        pToken.burn(stakingPollContractAddress, stakingBalToBeBurned);

        // Total burned token;
        totalBurnedToken = totalBurnedToken + stakingBalToBeBurned;
        stakingBalToBeBurned = 0;
    }

    //mint all the unstaked token to respective users.

    function mintUnstakedTokens() public {
        // Only owner can call this function
        require(msg.sender == owner, "caller must be the owner");

        require(stakingBalToBeBurned == 0, "Need to burn all the token");
        // Issue tokens to all unStakers
        for (uint256 i = 0; i < unStakers.length; i++) {
            address recipient = unStakers[i];
            uint256 balance = unStakedUserDetails[recipient];
            if (balance > 0) {
                pToken.mint(recipient, balance);
                delete unStakedUserDetails[recipient];
                delete hasUnstaked[recipient];
                totalMintedToken = totalMintedToken + balance;
            }
        }
        delete unStakers;
    }

    // find the percentage for each staked users.
    function getPercentage(uint256 _investedAmount, uint256 _tempTotalStaking)
        internal
        pure
        returns (uint256 percentage)
    {
        return ((_investedAmount * 1000000000000) / _tempTotalStaking);
        // return (tempPercentage * 100);
    }

    // Mint the remaining token to respective user after the project completed.
    function mintRemainingToken(
        uint256 _totalProjectToken,
        address projectOwnerAddress
    ) public {
        // Only owner can call this function
        require(msg.sender == owner, "caller must be the owner");

        // Need to burn all the token
        require(stakingBalToBeBurned == 0, "Need to burn all the token");

        // Check the total staked token is equal or greater than total project token.
        uint256 tempTotalStaking = totalStakeBalance();
        require(
            tempTotalStaking >= _totalProjectToken,
            "total Staked is should not be lesser than project token."
        );

        uint256 projectCompletedMintedToken;

        // Issue tokens to all stakers
        for (uint256 i = 0; i < stakers.length; i++) {
            address recipient = stakers[i];
            uint256 percentage = getPercentage(
                stakingBalance[recipient],
                tempTotalStaking
            );
            uint256 allocatedTokens = (_totalProjectToken * percentage) /
                1000000000000;
            uint256 balance = (stakingBalance[recipient] - allocatedTokens);
            if (balance > 0) {
                pToken.mint(recipient, balance);
                delete stakingBalance[recipient];
                delete hasStaked[recipient];
                totalMintedToken = totalMintedToken + balance;
                projectCompletedMintedToken =
                    projectCompletedMintedToken +
                    balance;
            }
        }
        delete stakers;

        // Sending the remaing token staked to project owner.
        // check the totalstaking - projectCompletedMintedToken.
        uint256 sendTokenToProjectOwner = tempTotalStaking -
            projectCompletedMintedToken;
        // require(
        //     sendTokenToProjectOwner == _totalProjectToken,
        //     "calculation mistake.."
        // );
        pToken.mint(projectOwnerAddress, sendTokenToProjectOwner);
    }

    // Using mint function return all the remaining tokens to staked users wallet.

    // function mintRemainingToken(mapping(address => uint256) sendWalletAddresses)
    //     public
    //     returns (bool success)
    // {
    //     // Only owner can call this function
    //     require(msg.sender == owner, "caller must be the owner");
    // }

    // Issuing Tokens
    // function issueTokens() public {
    //     // Only owner can call this function
    //     require(msg.sender == owner, "caller must be the owner");

    //     // Issue tokens to all stakers
    //     for (uint256 i = 0; i < stakers.length; i++) {
    //         address recipient = stakers[i];
    //         uint256 balance = stakingBalance[recipient];
    //         if (balance > 0) {
    //             dappToken.transfer(recipient, balance);
    //         }
    //     }
    // }
}
